/** * jQuery Line Progressbar * Version: 1.0.0 */ ;
(function($) {
    'use strict';
    $.fn.rProgressbar = function(options) {
        options = $.extend({ percentage: null, ShowProgressCount: true, duration: 1000, fillBackgroundColor: '#ed1c24', backgroundColor: '#EEEEEE', borderRadius: '10px', height: '7px', width: '100%' }, options);
        $.options = options;
        return this.each(function(index, el) {
            $(el).html('<div class="progressbar"><div class="proggress"><div class="percentCount"></div></div></div>');
            var lineProgressBarInit = function() {
                var progressFill = $(el).find('.proggress');
                var progressBar = $(el).find('.progressbar');
                progressFill.css({ 
                    backgroundColor: options.fillBackgroundColor, 
                    height: options.height, 
                    borderRadius: options.borderRadius 
                });
                progressBar.css({ 
                    width: options.width,  
                    borderRadius: options.borderRadius 
                });
                progressFill.animate({ 

                    // we can use the % symbol instead o blank "" , if you want to show result in %
                    width: options.percentage + "" 
                }, { 
                    step: function(x) { 
                        if (options.ShowProgressCount) { 
                            $(el).find(".percentCount").text(Math.round(x) + ""); 
                        } }, duration: options.duration });
            }
            $(this).waypoint(lineProgressBarInit, { offset: '100%', triggerOnce: true });
        });
    }
})(jQuery);
